var querystring = require('querystring');

module.exports = function (getRequest, apiKey) {
  return {
    /**
     * Find the block reward for a given address and block
     * @param {string} address - Address of the block
     * @param {string} blockno - Block number
     * @returns {Promise.<object>}
     */
    getblockreward: function getblockreward(address, blockno) {
      var module = 'block';
      var action = 'getblockreward';

      if (!blockno) {
        blockno = 0;
      }

      var query = querystring.stringify({
        module: module,
        action: action,
        address: address,
        blockno: blockno,
        apiKey: apiKey
      });
      return getRequest(query);
    }
  };
};
