import React, { Component } from 'react'
//import { useState } from 'react'
import './App.css'
import Switch from 'react-ios-switch';
import FormData from 'form-data';

//import Identicon from 'identicon.js';
//var QRCode = require('qrcode.react');

//Exhange contract v0
//0x8B68984546d5BE5089eBf791533a10267df8D107
//exchange contract v0.1
//0x26a0f03E7A43C1cd77029e0B902DcBfFB5e1E9Fa
//0x26a0f03E7A43C1cd77029e0B902DcBfFB5e1E9Fa

const pinataSDK = require('@pinata/sdk');
const pinata = pinataSDK('0f3f630bec73946940bd', 'c59ada21cf8e2eac1d19b2eb7177ff6d5d95f4c6a2b962a6d74959c3a7b132e9');
const axios = require('axios');

class NFTForm extends Component {

        constructor(props) {
          super(props);
          this.state = { 
            account : "",
            seconds : 0,
            imgBuffer : "",
            revealImg: false,
            revealImg2: false,
            image: null,
            imageName: null,
            mint_market : true,
            //IPFS
            ipfsByteCount : "0",
            pinataConnection : false,
            nftCount : "",
            setFile : "",
            nftName : "",
            ipfsHash : "NONE",
            ipfsJSONHash : "",
            tokenUri : "",

            //nft contract
            nftMintAddress : "0x760e90639425f52c0295987BfB99046E4CFD72C3",//"0x8A422D1Ea395721423eF544aC95c316e9CbF37f8",
            nftMintAbi : [
              {
                "inputs": [],
                "stateMutability": "nonpayable",
                "type": "constructor"
              },
              {
                "anonymous": false,
                "inputs": [
                  {
                    "indexed": true,
                    "internalType": "address",
                    "name": "_to",
                    "type": "address"
                  },
                  {
                    "indexed": true,
                    "internalType": "uint256",
                    "name": "_tokenId",
                    "type": "uint256"
                  },
                  {
                    "indexed": false,
                    "internalType": "string",
                    "name": "_ipfsHash",
                    "type": "string"
                  }
                ],
                "name": "Mint",
                "type": "event"
              },
              {
                "anonymous": false,
                "inputs": [
                  {
                    "indexed": true,
                    "internalType": "address",
                    "name": "_from",
                    "type": "address"
                  },
                  {
                    "indexed": true,
                    "internalType": "address",
                    "name": "_to",
                    "type": "address"
                  },
                  {
                    "indexed": true,
                    "internalType": "uint256",
                    "name": "_tokenId",
                    "type": "uint256"
                  }
                ],
                "name": "Transfer",
                "type": "event"
              },
              {
                "inputs": [
                  {
                    "internalType": "address",
                    "name": "",
                    "type": "address"
                  }
                ],
                "name": "OwnerToHash",
                "outputs": [
                  {
                    "internalType": "string",
                    "name": "",
                    "type": "string"
                  }
                ],
                "stateMutability": "view",
                "type": "function"
              },
              {
                "inputs": [
                  {
                    "internalType": "string",
                    "name": "_ipfsHash",
                    "type": "string"
                  }
                ],
                "name": "mint",
                "outputs": [],
                "stateMutability": "payable",
                "type": "function"
              },
              {
                "inputs": [],
                "name": "name",
                "outputs": [
                  {
                    "internalType": "string",
                    "name": "",
                    "type": "string"
                  }
                ],
                "stateMutability": "view",
                "type": "function"
              },
              {
                "inputs": [],
                "name": "nftCounter",
                "outputs": [
                  {
                    "internalType": "uint256",
                    "name": "",
                    "type": "uint256"
                  }
                ],
                "stateMutability": "view",
                "type": "function"
              },
              {
                "inputs": [
                  {
                    "internalType": "address",
                    "name": "_to",
                    "type": "address"
                  },
                  {
                    "internalType": "uint256",
                    "name": "_tokenId",
                    "type": "uint256"
                  }
                ],
                "name": "transfer",
                "outputs": [],
                "stateMutability": "nonpayable",
                "type": "function"
              }
            ],
            nftMintContract : "",
            nftMintName : ""

          };
          //this.loadMintContract = this.loadMintContract.bind(this);
        }

        async loadMintContract()
            {
            const web3 = window.web3
            const bsChain = web3.eth

            const accounts = await bsChain.getAccounts()
            //console.log("accounts :: ", accounts[0]);
            this.setState({account : accounts[0]});

            this.state.nftMintAddress = web3.utils.toChecksumAddress(this.state.nftMintAddress);
            const nftMintContract = new bsChain.Contract(this.state.nftMintAbi, this.state.nftMintAddress);
            this.setState({nftMintContract});

            let nftMintName = await nftMintContract.methods.name().call();
            this.setState({nftMintName}); 
            console.log("nftMintName :: ",nftMintName);

            //TAG call the keys from .env
            const API_KEY = '0f3f630bec73946940bd';
            const API_SECRET = 'c59ada21cf8e2eac1d19b2eb7177ff6d5d95f4c6a2b962a6d74959c3a7b132e9';

            // the endpoint needed to upload the file
            const url =  `https://api.pinata.cloud/data/userPinnedDataTotal`

            const response = await axios.get(
              url,
              {
                headers: {
                    'pinata_api_key': API_KEY,
                    'pinata_secret_api_key': API_SECRET
                }
              }
              )
            console.log( " response :: ",response.data.pin_size_total)
          // get the hash
          this.setState({ipfsByteCount : response.data.pin_size_total / 100})

            }



        /*reveal(){
          //this.setState({revealImg : !this.state.revealImg});
          this.setState(state => ({
            revealImg: !this.state.revealImg
        }));
