var querystring = require('querystring');

module.exports = function (getRequest, apiKey) {
  return {
    /**
     * Returns the supply of Tokens
     * @param {string} tokenname - Name of the Token
     * @param {string} contractaddress - Address from token contract
     * @example
     * var supply = api.stats.tokensupply(null, '0x57d90b64a1a57749b0f932f1a3395792e12e7055');
     * @returns {Promise.<object>}
     */
    tokensupply: function tokensupply(tokenname, contractaddress) {
      var module = 'stats';
      var action = 'tokensupply';
      var params = {
        module: module,
        action: action,
        apiKey: apiKey
      };

      if (tokenname) {
        params.tokenname = tokenname;
      }

      if (contractaddress) {
        params.contractaddress = contractaddress;
      }

      var query = querystring.stringify(params);
      return getRequest(query);
    },

    /**
     * Returns total supply of ether
     * var supply = api.stats.ethsupply();
     * @returns {Promise.<integer>}
     */
    ethsupply: function ethsupply() {
      var module = 'stats';
      var action = 'ethsupply';
      var query = querystring.stringify({
        module: module,
        action: action,
        apiKey: apiKey
      });
      return getRequest(query);
    },

    /**
     * Returns the price of ether now
     * @example
     * var price = api.stats.ethprice();
     * @returns {Promise.<integer>}
     */
    ethprice: function ethprice() {
      var module = 'stats';
      var action = 'ethprice';
      var query = querystring.stringify({
        module: module,
        action: action,
        apiKey: apiKey
      });
      return getRequest(query);
    }
  };
};
